import React from 'react'

export default class Counter extends React.Component {
    constructor(props) {
        super(props) 
        this.state = {
            count: 0
        }

        this.increment = this.increment.bind(this)
    }

    // All set state calls are batched into one update in order to prevent unnecessary rerenders. This makes setState asynchronous,
    // because of this you cannot rely on current state to calculate next state, such as in a counter. 
   
    increment() {
        
        this.setState({count: this.state.count + this.props.increment})
        this.setState((prev, props) => ({count: prev.count + props.increment}))
        this.setState({count: this.state.count + this.props.increment})
        this.setState((prev, props) => ({count: prev.count + props.increment}))
        console.log(this.state.count)
        // There is a callback you can pass to setState, it takes in the previous state from the output of last setState call, and the 
        // current props at time of update as inputs
        
        // this.setState((prev, props) => ({count: prev.count + props.increment}))
        // this.setState((prev, props) => ({count: prev.count + props.increment}))

        
    }

    render() {
        return (
            <div>
                <button>-</button>
                <h1>{this.state.count}</h1>
                <button onClick={this.increment}>+</button>
            </div>
        )
    }
}
